when.expression-component:


					import java.security.MessageDigest;
					import java.math.BigInteger;						
					import org.dom4j.*;					

					document = message.payload;

					String xmlData = document.asXML();
					//change namespace
					xmlData = xmlData.replaceAll("\"ACME\"","\"ACMEv1\"");

					//generate UUID -- Data
					MessageDigest md = MessageDigest.getInstance("SHA-256");
					md.update(xmlData.getBytes());
					BigInteger number = new BigInteger(1, md.digest());
      				String data = number.toString(16);

					//Ruta exacta para asegurarme que lo agrego en el lugar correcto.
					Node transNode = document.selectSingleNode( "/*[local-name()='Envelope']/*[local-name()='Body']/*[local-name()='Transactions']" );

					//Solo agrego el nodo si no existe.
					if(transNode.selectSingleNode("./*[local-name()='messageId']")==null){													
						Element messageIdNode = transNode.addElement("messageId");                     												
						messageIdNode.setText("legacy-" + data);						
					}

					String nuevoXML = document.asXML();

					//Reemplazo namespace nuevamente
					nuevoXML = nuevoXML.replaceAll("\"ACME\"","\"ACMEv1\"");
					//transformo a doc
					Document newDocument = DocumentHelper.parseText(nuevoXML);
					message.payload = newDocument;
